def clicked_solve():
    values = [x[:] for x in cells]
    for i in range(9):
        for j in range(9):
            if cells[i][j].get() != "":
                values[i][j] = int(cells[i][j].get())
                cells[i][j].config(state='disabled')
            else:
                values[i][j] = 0
    response, new_values = main.Run(values)
    if response:
        for i in range(9):
            for j in range(9):
                result_string_sheet[i][j].set(new_values[i][j])
                cells[i][j].config(validate="key")
                cells[i][j].config(validatecommand=(cells[i][j].register(checkVal), '%d', '%P', '%S'))
    else:
        messagebox.showwarning('Attention', 'Unsolvable')
        for i in range(9):
            for j in range(9):
                cells[i][j].config(state='normal')


def clicked_clear():
    for i in range(9):
        for j in range(9):
            if cells[i][j]['state']  != "disabled":
                result_string_sheet[i][j].set('')
            cells[i][j].config(state='normal')
            cells[i][j].config(validate="key")
            cells[i][j].config(validatecommand=(cells[i][j].register(checkVal), '%d', '%P', '%S'))


def checkVal(d, P, S):
    #print(d, P, S)
    if (d == '1' and len(P) == 1 and S.isdigit() and S != '0') or d == '0':
            return True
    return False


def on_key_press(event):
    if event.keysym == 'Return':
        btn_solve.invoke()
    if event.keysym == 'Delete':
        btn_clr.invoke()
    if event.keysym == 'Escape':
        return root.destroy()
    btn_solve.config(state='disabled')
    btn_clr.config(state='disabled')
    if root.focus_get()["state"] != 'disabled':
        if event.keysym == 'Left':
            root.focus_get().event_generate('<<PrevWindow>>')
        elif event.keysym == 'Right':
            root.focus_get().event_generate('<<NextWindow>>')
        elif event.keysym == 'Up':
            f_i = root.focus_get().grid_info()['row'] - 1
            f_j = root.focus_get().grid_info()['column']
            if f_i == 4 or f_i == 8:
                f_i -= 1
            if f_i == 0:
                f_i = 11
            while root.grid_slaves(f_i, f_j)[0]["state"] != 'normal':
                f_i -= 1
                if f_i == 4 or f_i == 8:
                    f_i -= 1
                if f_i == 0:
                    f_i = 11
            root.grid_slaves(f_i, f_j)[0].focus_set()
            root.grid_slaves(f_i, f_j)[0].selection_range(0, 1)
        elif event.keysym == 'Down':
            f_i = root.focus_get().grid_info()['row'] + 1
            f_j = root.focus_get().grid_info()['column']
            if f_i == 4 or f_i == 8:
                f_i += 1
            if f_i == 12:
                f_i = 1
            while root.grid_slaves(f_i, f_j)[0]["state"] != 'normal':
                f_i += 1
                if f_i == 4 or f_i == 8:
                    f_i += 1
                if f_i == 12:
                    f_i = 1
            root.grid_slaves(f_i, f_j)[0].focus_set()
            root.grid_slaves(f_i, f_j)[0].selection_range(0, 1)
    btn_solve.config(state='normal')
    btn_clr.config(state='normal')


